def convert(input_data: Dict[str, Any]) -> Dict[str, Any]:
    return {
        'user_input': f'Good morning! Please provide my daily briefing for {input_data.get("trigger_time", "today")}. Include overdue tasks, today\'s priorities, project updates, and suggestions for optimization.',
        'context': {
            'trigger_type': 'morning_briefing',
            'trigger_time': input_data.get('trigger_time'),
            'execution_id': input_data.get('execution_id'),
            'timezone': input_data.get('timezone', 'UTC'),
            'request_type': 'daily_briefing',
            'cron_expression': input_data.get('cron_expression'),
            'automated': True
        }
    }

def convert(input_data: Dict[str, Any]) -> Dict[str, Any]:
    ai_response = input_data.get('extracted_data', {})
    notion_actions = ai_response.get('notion_actions', [])

    # Find task-related actions
    task_actions = [action for action in notion_actions 
                   if action.get('database_id') == '27c0b1df-411b-81fa-ac40-ca8f7b697a0b']

    if not task_actions:
        return {'skip_execution': True}

    # Process the highest priority task action
    task_action = sorted(task_actions, key=lambda x: x.get('priority', 999))[0]
    action_type = task_action.get('action_type', 'create_database_entry')

    result = {
        'data': {
            'action_type': action_type,
            'database_id': '27c0b1df-411b-81fa-ac40-ca8f7b697a0b',
            'entry_id': task_action.get('entry_id'),
            'properties': task_action.get('properties', {})
        },
        'context': {
            'ai_reasoning': task_action.get('reason', ''),
            'action_priority': task_action.get('priority', 1),
            'total_actions': len(notion_actions),
            'source': 'ai_agent_task_management'
        }
    }

    return result

