def convert(input_data: Dict[str, Any]) -> Dict[str, Any]:
    ai_response = input_data.get('extracted_data', {})
    notion_actions = ai_response.get('notion_actions', [])

    if not notion_actions:
        return {'skip_execution': True}

    # Take the first notion action
    first_action = notion_actions[0]
    action_type = first_action.get('action_type', 'create_database_entry')

    result = {
        'data': {
            'action_type': action_type,
            'database_id': first_action.get('database_id', 'your-projects-database-id'),
            **first_action.get('data', {})
        },
        'context': {
            'ai_reasoning': first_action.get('reason', ''),
            'total_actions': len(notion_actions),
            'source': 'ai_agent_structured_output'
        }
    }

    return result
